<?php

namespace App\Http\Controllers;

use Illuminate\Http\Request;
use Illuminate\Support\Facades\Auth;
use App\Plants;

class AdminController extends Controller
{
    /**
     * Create a new controller instance.
     *
     * @return void
     */
    public function __construct()
    {
        $this->middleware('auth');
    }

    public function add(){
        $user = Auth::user();
        return view('add', ['user' => $user]);
    }

    public function edit($id){
        $plant = Plants::findOrFail($id);
        $user = Auth::user();
        return view('edit', ['plant' => $plant, 'user' => $user]);
    }

    public function post(Request $request){
        $this->validate($request, [
            'name' => 'bail|required|max:30',
            'latinName' => 'required|max:40',
            'description' => 'required',
            'type' => 'required|max:20',
            'flowers' => 'required',
            'winterHard' => 'required|numeric',
            'height' => 'required|numeric',
            'moisture' => 'required|max:10',
            'light' => 'required|max:30',
            'groundType' => 'required',
            'edible' => 'required',
            'datePurchase' => 'required',
            'picture' => 'mimes:jpg,png,jpeg|max:3000'
      ]);

      $picture = '';

      //Set default picture
      if($request->file('picture') === null){
          $picture = 'data:image/jpeg;base64,';
      }
      //User has selected a picture
      else{
          $picture = 'data:image/png;base64,' . base64_encode(file_get_contents($request->file('picture')));
      }
      //save to database
      $plant = new Plants;
      $plant->fill($request->all());
      $plant->picture = $picture;
      $plant->save();

      return redirect('/plant/' . $plant->id);
    }

    public function update(Request $request, $id){
        $this->validate($request, [
           'name' => 'bail|required|max:30',
           'latinName' => 'required|max:40',
           'description' => 'required',
           'type' => 'required|max:20',
           'flowers' => 'required',
           'winterHard' => 'required|numeric',
           'height' => 'required|numeric',
           'moisture' => 'required|max:10',
           'light' => 'required|max:30',
           'groundType' => 'required',
           'edible' => 'required',
           'datePurchase' => 'required',
           'picture' => 'mimes:jpg,png,jpeg|max:3000'
        ]);

      $plant = Plants::findOrFail($id);
      $plant->fill($request->all());
      $plant->save();

      //new picture selected
      if($request->file('picture') != null){
          $picture = 'data:image/png;base64,' . base64_encode(file_get_contents($request->file('picture')));
          $plant->picture = $picture;
          $plant->save();
      }

      return redirect('/plant/' . $id);
    }

    public function delete(Request $request){
        Plants::destroy($request->id);
        return redirect('/');
    }
}
